      var fs = require('fs');
      function readAdir(dir, filter, callback) {
            fs.readdir(dir, function (err, data) {
                  if (err) {
                        // 将错误传给回调函数处理
                        return callback(err);
                  } else {
                        var reg = new RegExp('\\.' + filter + '$');
                        var newData = [];
                        for (var i = 0; i < data.length; i++) {
                              if (reg.test(data[i])) {
                                    newData.push(data[i]);
                              }
                        }
                        // 将处理后的数据传给回调函数处理
                        callback(null, newData);
                  }
            });
      }
      // 导出模块
      module.exports = readAdir;


